'use strict';
import uuid from 'uuid';
import {
  Event
}
from '@hoist/model';
import {
  filter
}
from 'lodash';
export class Authorisation {
  constructor(request, reply, bouncerToken, publisher) {
    this._request = request;
    this._reply = reply;
    this._bouncerToken = bouncerToken;
    this.query = request.query;
    this.payload = request.payload;
    this.key = bouncerToken.key;
    this._publisher = publisher;
  }
  set(key, value, callback) {
    this._bouncerToken.state[key] = value;
    this._bouncerToken.markModified('state');
    return this._bouncerToken.saveAsync().then(() => {
      return this;
    }).nodeify(callback);
  }
  setDisplayProperty(name, value, callback) {
    var existingProperty = filter(this._bouncerToken.displayProperties, (prop) => {
      return prop.name.toLowerCase() === name.toLowerCase();
    });
    if (existingProperty.length !== 0) {
      existingProperty[0].value = value;
    } else {
      this._bouncerToken.displayProperties.push({
        name: name,
        value: value
      });
    }
    return this._bouncerToken.saveAsync().then(() => {
      return this;
    }).nodeify(callback);
  }
  get(key) {
    return this._bouncerToken.state[key];
  }
  delete(key, callback) {
    delete this._bouncerToken.state[key];
    this._bouncerToken.markModified('state');
    return this._bouncerToken.saveAsync().then(() => {
      return this;
    }).nodeify(callback);
  }
  raise(name, payload) {
    let event = new Event({
      eventId: uuid.v4().split('-').join(''),
      applicationId: this._bouncerToken.application,
      eventName: name,
      environment: 'live',
      correlationId: require('uuid').v4(),
      payload: payload
    });
    return this._publisher.publish(event);
  }
}

'use strict';
import logger from '@hoist/logger';
import errors from '@hoist/errors';
import { Authorization } from './authorization';
import fs from 'fs';
import path from 'path';
import config from 'config';
import { BouncerToken, ConnectorSetting } from '@hoist/model';
import { Publisher } from '@hoist/broker';

export class ConnectorHandler {
  constructor() {
    this._logger = logger.child({
      cls: this.constructor.name
    });
    this._publisher = new Publisher();
  }
  _loadConnector(connectorSettings) {
    return Promise.resolve()
      .then(() => {
        var connectorPath = path.resolve(path.join(config.get('Hoist.filePaths.connectors'), connectorSettings.connectorType, 'current'));
        /*istanbul ignore if */
        if (!fs.existsSync(connectorPath)) {
          throw new errors.connector.InvalidError();
        }

        var realpath = fs.readlinkSync(connectorPath);
        logger.info({
          connectorPath: connectorPath,
          realpath: realpath
        }, 'connectorPath');
        return require(realpath);
      })
      .then((Connector) => {
        connectorSettings.settings._connectorKey = connectorSettings.key;
        connectorSettings.settings._applicationId = connectorSettings.application;
        return new Connector(connectorSettings.settings);
      });
  }
  onRequest(request, reply) {
    this._logger.info('starting connector request');
    return Promise.resolve()
      .then(() => {
        return BouncerToken.findOneAsync({
          key: request.params.connectorKey
        });
      }).then((bouncerToken) => {
      if (!bouncerToken) {
        throw new errors.Http404Error('invalid key');
      }
      return ConnectorSetting.findOneAsync({
        application: bouncerToken.application,
        environment: bouncerToken.environment,
        key: bouncerToken.connectorKey
      }).then((connectorSettings) => {
        if (!connectorSettings) {
          throw new errors.Http500Error('unable to find connector settings');
        }
        return this._loadConnector(connectorSettings);
      }).then((connector) => {
        if (!connector) {
          throw new errors.Http500Error('unable to load connector');
        }
        if (!connector.intercept) {
          return reply({
            ok: true
          });
        }
        return connector.intercept(new Authorization(request, reply, bouncerToken, this._publisher))
          .then(function() {
            return reply({
              ok: true
            });
          });
      });
    }).catch((err) => {
      this._logger.error(err);
      reply(err);
    });

  }
}

'use strict';
import errors from '@hoist/errors';
import logger from '@hoist/logger';
import Boom from 'boom';
import Moment from 'moment';
import {
  routeFromPath
}
from './url_helpers';
import {
  Publisher
}
from '@hoist/broker';
import {
  Organisation, Application, EventMetric, Event
}
from '@hoist/model';
import {
  clone, isObject, extend
}
from 'lodash';
import uuid from 'uuid';

/**
 * class to handle incoming requests
 */

export class EndpointHandler {
  /**
   * create new handler
   */
  constructor() {
    this._logger = logger.child({
      cls: this.constructor.name
    });
    this._publisher = new Publisher();
  }

  /**
   * handle incoming requests and turn them into events
   * @returns {Promise}
   */
  onRequest(req, reply) {
    this._logger.info('got a webhook request');
    return Organisation.findOneAsync({
        slug: req.params.orgSlug
      }).then((organisation) => {
        if (!organisation) {
          throw new errors.model.NotFoundError();
        }
        return Application.findOneAsync({
          slug: req.params.appSlug,
          organisation: organisation._id
        });
      }).then((application) => {
        if (!application) {
          throw new errors.model.NotFoundError();
        }
        if ((!application.settings.live) || (!application.settings.live.endpoints)) {
          throw new errors.Http404Error('No Endpoint Found');
        }
        this._logger.info({
          application: application._id
        }, 'found org and app');
        req.params.endpoint = '/' + req.params.endpoint;
        return Promise.resolve(routeFromPath(application.settings.live.endpoints, req.method, req.params.endpoint))
          .then((route) => {
            this._logger.info({
              application: application._id,
              route: route,
              endpoint: 'req.params.endpoint'
            }, 'mapped route');
            if (!route) {
              throw new errors.Http404Error('No Endpoint Found');
            }
            this._logger.info('got an endpoint');
            var payload = clone(route, true);
            delete payload.event;
            delete payload.method;
            delete payload.authenticate;
            if (req.method.toLowerCase() === 'post') {
              if (isObject(req.payload)) {
                payload = extend(payload, req.payload);
              }
            }

            payload._request = {
              body: req.payload,
              headers: req.headers,
              url: req.params.endpoint,
              method: req.method
            };

            var queryString = req.url.search;

            if (queryString) {
              payload._request.queryString = queryString;
              payload._request.query = req.url.query;
            }
            var priority = req.headers['x-priority'] || 3;
            delete payload._request.headers.host;
            delete payload._request.headers['x-priority'];
            this._logger.info('building event');
            var ev = new Event({
              eventId: uuid.v4().split('-').join(''),
              applicationId: application._id,
              eventName: route.event,
              environment: 'live',
              correlationId: require('uuid').v4(),
              payload: payload,
              priority: priority
            });
            return ev;
          });
      })
      .then((event) => {
        this._logger.info({
          event: event
        }, 'sending event to broker');

        return this._publisher.publish(event)
          .then(() => {
            var raisedDate = new Moment();
            var update = {
              $inc: {}
            };
            update.$inc.totalRaised = 1;
            update.$inc['raised.' + raisedDate.utc().minutes()] = 1;
            EventMetric.updateAsync({
              application: event.applicationId,
              environment: 'live',
              eventName: event.eventName,
              timestampHour: raisedDate.utc().startOf('hour').toDate()
            }, update, {
              upsert: true
            }).catch(function (err) {
              this._logger.alert(err);
              this._logger.error(err);
            });
            return event;
          });
      })
      .then((event) => {
        this._logger.info('sending reply');
        var response = reply(event.toObject());
        response.header('x-hoist-cid', event.correlationId);
        response.header('x-hoist-eid', event.eventId);
        response.statusCode = 200;
      }).catch((err) => {
        this._logger.error(err);
        if (!errors.isHoistError(err)) {
          console.log(err);
          this._logger.alert(err);
          err = new errors.HoistError();
        }
        this._logger.info('sending error reply');
        reply(Boom.wrap(err, parseInt(err.code)));
      });
  }
}

'use strict';
import { EndpointHandler } from './endpoint_handler';
import logger from '@hoist/logger';
import { ConnectorHandler } from './connector_handler';

/**
 * maps routes to handlers for this server
 */

export class Router {
  /**
   * create a new router
   */
  constructor() {
    this._logger = logger.child({
      cls: this.constructor.name
    });
    this._endpointHandler = new EndpointHandler();
    this._connectorHandler = new ConnectorHandler();
  }

  /**
   * map routes to endpoints on the server
   */
  mapRoutes(server) {
    server.route({
      path: '/connector/{connectorKey}/{endpoint*}',
      handler: this._connectorHandler.onRequest,
      config: {
        bind: this._connectorHandler,
        payload: {
          parse: true,
          maxBytes: '2097152',
          output: 'data'
        }
      },
      method: ['*']
    });
    server.route({
      path: '/{orgSlug}/{appSlug}/{endpoint*}',
      handler: this._endpointHandler.onRequest,
      config: {
        bind: this._endpointHandler,
        payload: {
          parse: true,
          maxBytes: '2097152',
          output: 'data'
        }
      },
      method: ['*']
    });
  }
}

'use strict';
import Hapi from 'hapi';
import config from 'config';
import { _mongoose } from '@hoist/model';
import { Router } from './router';
import logger from '@hoist/logger';
import Bluebird from 'bluebird';
Bluebird.promisifyAll(_mongoose);


/**
 * the Hoist endpoint server
 */
export class Server {
  /**
   * create a new instance of the server
   */
  constructor() {
    this._logger = logger.child({
      cls: this.constructor.name
    });
    this._router = new Router();
  }
  _createHapiServer() {
    this._logger.info('creating hapi server');
    this._hapi = new Hapi.Server();
    Bluebird.promisifyAll(this._hapi);
    this._hapi.connection({
      host: config.get('Hoist.server.host'),
      port: config.get('Hoist.server.port')
    });
    this._router.mapRoutes(this._hapi);
  }

  /**
   * start the server
   * @returns {Promise}
   */
  start() {
    return _mongoose.connectAsync(config.get('Hoist.mongo.core.connectionString'))
      .then(() => {
        this._createHapiServer();
        return this._hapi.startAsync()
          .then(() => {
            logger.info('server started');
          });
      });
  }

  /**
   * stop the server
   * @returns {Promise}
   */
  stop() {
    this._logger.info('stoping server');
    return Promise.resolve()
      .then(() => {
        if (this._hapi) {
          this._logger.info('server shutting down');
          return this._hapi.stopAsync().then(() => {
            delete this._hapi;
            return _mongoose.disconnectAsync().then(() => {
              logger.info('server shutdown');
            });
          });
        }
      });
  }
}

'use strict';
import { Router } from 'routes';
import { keys, assign, some } from 'lodash';
RegExp.escape = function(s) {
  return s.replace(/[-\/\\^$*+?.()|[\]{}]/g, '\\$&');
};

/**
 * maps a specific path to the correct route within an apps settings
 */
export function routeFromPath(endpoints, method, path) {
  var router = new Router();
  if (endpoints && path && method) {

    var routes = keys(endpoints);
    if (routes.length > 0) {
      routes.forEach((r) => {
        router.addRoute(r, () => {
          return endpoints[r];
        });
      });
      var match = router.match(path);
      if (match) {
        var endpoint = match.fn();
        var methods = [].concat(endpoint.methods);

        if (some(methods, function(m) {
            return m.toLowerCase() === method.toLowerCase();
          })) {
          var params = assign(match.params, endpoint);
          delete params.methods;
          return params;

        }
      }
    }
  }
  return null;
}

